ContainerName.value:
@concat(activity('Filtered Metadata Row').output.Value[0].ArchiveContainer
    , '/'
    , activity('Filtered Metadata Row').output.Value[0].BronzeFolder
    , '/'
    , activity('Filtered Metadata Row').output.Value[0].Source
)

Filename.value:
@concat(
    replace(pipeline().parameters.Filename
        , activity('Filtered Metadata Row').output.Value[0].FileType
        , ''
    )
    , '/'
    , formatDateTime(pipeline().TriggerTime, 'yyyy-MM-dd-HH-mm')
    , '_'
    , activity('Filtered Metadata Row').output.Value[0].FileName
)

expression.value:
@not(
    or( equals(coalesce(activity('Filtered Metadata Row').output.Value[0].GoldNoteBookName, 'null'), 'null')
        , empty(activity('Filtered Metadata Row').output.Value[0].GoldNoteBookName)
    )
)

FileName.value:
@if( and(
        equals(coalesce(item().TargetFile, 'null'), 'null')
        , equals(coalesce(item().TargetType, 'null'), 'null')
    )
    , ''
    , concat(item().TargetFile
        , '_'
        , formatDateTime(utcnow(), 'HHmmss')
        , '.'
        , item().TargetType
    )
)
